pragma solidity ^0.8.0;

contract ADFVToken is ERC20 {
    string public constant name = "Automation Displacement Future Vision Token";
    string public constant symbol = "ADFV";
    uint8 public constant decimals = 18;
    uint256 public totalSupply;

    constructor() public {
        totalSupply = 100000000 * (10 ** uint256(decimals));
    }

    function transfer(address _to, uint256 _value) public returns (bool) {
        require(balanceOf(msg.sender) >= _value && _value > 0, "Transfer failed: insufficient balance");
        require(_to != address(0), "Transfer failed: invalid recipient");
        _transfer(msg.sender, _to, _value);
        return true;
    }

    function balanceOf(address _owner) public view returns (uint256) {
        return balances[_owner];
    }

    function approve(address _spender, uint256 _value) public returns (bool) {
        require(_spender != address(0), "Approve failed: invalid spender");
        approvals[_spender] = _value;
        emit Approval(msg.sender, _spender, _value);
        return true;
    }

    function transferFrom(address _from, address _to, uint256 _value) public returns (bool) {
        require(balanceOf(_from) >= _value && _value > 0, "TransferFrom failed: insufficient balance");
        require(_to != address(0), "TransferFrom failed: invalid recipient");
        require(approvals[_from] >= _value, "TransferFrom failed: insufficient approval");
        _transfer(_from, _to, _value);
        approvals[_from] -= _value;
        return true;
    }

    mapping(address => uint256) private balances;
    mapping(address => uint256) private approvals;

    event Transfer(address indexed _from, address indexed _to, uint256 _value);
    event Approval(address indexed _owner, address indexed _spender, uint256 _value);

    function _transfer(address _from, address _to, uint256 _value) internal {
        balances[_from] -= _value;
        balances[_to] += _value;
        emit Transfer(_from, _to, _value);
    }
}
